const Coursespage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCourse, setSelectedCourse] = useState(null);

  const filteredCourses = courseData.filter(course =>
    course.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="courses-container">
      <h2 className="heading">We Are Best About This Job Solution.</h2>
      <p className="sub-text">Search and discover the best courses to level up your skills.</p>

      <input
        type="text"
        placeholder="Search courses..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-bar"
      />

      <div className="course-grid">
        {filteredCourses.map((course) => (
          <div key={course.id} className="course-card" onClick={() => setSelectedCourse(course)}>
            <div className="date-box">
              <span>{course.date}</span>
              <small>{course.month}</small>
            </div>
            <img src={course.image} alt={course.title} />
            <h4>{course.title}</h4>
            <p>{course.description}</p>
          </div>
        ))}
      </div>

      {selectedCourse && (
        <div className="course-modal">
          <div className="modal-content">
            <span className="close-button" onClick={() => setSelectedCourse(null)}>&times;</span>
            <img src={selectedCourse.image} alt={selectedCourse.title} />
            <h2>{selectedCourse.title}</h2>
            <p><strong>Duration:</strong> {selectedCourse.duration}</p>
            <p>{selectedCourse.fullDescription}</p>
            <button className="buy-button">Buy Course</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Coursespage;